import React, { useState } from 'react';
import './App.css';
import sushi from './sushi.jpeg';
import c from './c.jpeg';
import ap from './ap.jpeg';
import hcb from './hcb.jpeg'; 


function App() {
    const [recipes, setRecipes] = useState([
        {
            title: 'Spaghetti Bolognese',
            ingredients: ['Spaghetti', 'Ground beef', 'Tomato sauce', 'Onion', 'Garlic', 'Olive oil', 'Salt', 'Pepper'],
            instructions: 'Cook the spaghetti according to the package instructions. In a separate pan, cook the ground beef until browned. Add the chopped onion and garlic and sauté until soft. Stir in the tomato sauce and simmer for 10-15 minutes. Serve the sauce over the spaghetti.',
        },
        {
            title: 'Chocolate Chip Cookies',
            ingredients: ['Flour', 'Sugar', 'Brown sugar', 'Butter', 'Eggs', 'Vanilla extract', 'Chocolate chips', 'Baking soda', 'Salt'],
            instructions: 'Preheat the oven to 350°F (175°C). In a bowl, cream together the butter and sugars. Add the eggs and vanilla extract and mix well. Stir in the flour, baking soda, and salt until combined. Fold in the chocolate chips. Drop spoonfuls of dough onto a baking sheet and bake for 10-12 minutes, until golden brown.',
        },
    ]);

    return (
        <div className="App">
            <header className="App-header">
		<img src={sushi} alt="SUSHI" className="sushi-image" />
                <h1>Neenu's Cookbook</h1>
            </header>
            <main>
                {recipes.map((recipe, index) => (
                    <div key={index} className="recipe-card">
                        <h2>{recipe.title}</h2>
                        <h3>Ingredients</h3>
                        <ul>
                            {recipe.ingredients.map((ingredient, idx) => (
                                <li key={idx}>{ingredient}</li>
                            ))}
                        </ul>
                        <h3>Instructions</h3>
                        <p>{recipe.instructions}</p>
                    </div>
                ))}
            </main>
        </div>
    );
}

export default App;



